            <span className='material-symbols-sharp' >delete</span>
          </button>
          <button type="button" onClick={() => handleRemove(title.column.id)}>
            Remove
          </button>
        </div>
      </div>
        <div className='list'>

        </div>    
    </div>

  )
}

export default Lists
