import React from 'react'

import './Styling/List.css'; 
import './Styling/Board.css';
import { useState } from 'react'
import { v4 as uuidv4 } from "uuid";

function Lists() {
  const [title, setTitle] = useState(''); 
  const [list, setList] = useState([]);

  function onChangeHandler(e) {
    setTitle(e.target.value); 
  }

  const submitTodoHandler = (e) => {
    // Prevents refreshing the browser when the button is clicked
    e.preventDefault();
    setList([
        ...list,{
            text: title, 
            id: uuidv4()
        }
    ]);
};

  return (
    <div className='card-wrapper'>
      <div className='card-header-wrapper'>

        <input 
        className='card-header' 
        type='text' placeholder='In Progress' 
        onChange={onChangeHandler}
        value={title}
        />
        <div className='button-wrapper'>
          <button className='edit-button'>
            <span className='material-symbols-sharp' onClick={submitTodoHandler}>check</span>
          </button>
          <button className='delete-button'>
            <span className='material-symbols-sharp' onClick={console.log(title)}>delete</span>
          </button>
        </div>
      </div>
        <div className='list'>

        </div>    
    </div>

  )
}

export default Lists
