import React from 'react'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd'

import Todo from './Todo'

function TodoList({ todos, setTodos }) {


  function handleOnDrageEnd (result) {
    const items = Array.from(todos);
    const [reorderedItem] =  items.slice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem); 

  }
  return (

    <div className='todo-container'>
      <DragDropContext onDragEnd={handleOnDrageEnd}>
        <Droppable droppableId='droppable'>
          {(provided) => (
          <ul className='todo-list-created' {...provided.droppableProps} ref={provided.innerRef} >
            {todos.map((todo, index) => (

                  <Todo 
                text={todo.text}
                setTodos={setTodos}
                todo={todo}
                todos={todos}
                index={index}
                />

            ))} 
        </ul>
          )} 


        </Droppable>
        <ul className='todo-list-in-progress'>

        </ul>
        <ul className='todo-list-done'>

        </ul>
        </DragDropContext>
    </div> 
  )
}

export default TodoList
