import Form from './Form';
import TodoList from './TodoList';



function Board() {

  const [inputText, setInputText] = useState(""); 
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState('all');
  const [lists, setLists] = useState({
    Created: [''],
    InProgress: [''],
    Done: ['']
  })


