import React from 'react'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd'
import Form from './Form';
import Todo from './Todo'




function TodoList({ todos, setTodos, setInputText, inputText }) {




  const handleDragEnd = (result, destination, source) => {
    const start = result.source.droppableId;
    const finish = result.destination.droppableId;

    // Return card if it is dropped outside a column. 
    if(!result.destination) return;

    // Moves the cards whithin the sam column. 
    if (start === finish) {
      const items = Array.from(todos); 
      const [reorderedItem] = items.splice(result.source.index, 1); 
      items.splice(result.destination.index, 0, reorderedItem);

      setTodos(items);
      }




    //TODO - move from one list to another -> first try: 
    if(result.destination.droppableId !== result.source.droppableId ) {
      const sourceClone = Array.from(source);
      const destClone = Array.from(destination);
      const [removed] = sourceClone.splice(sourceClone.index, 1);
      console.log(removed);

      destClone.splice(destClone.index, 0, removed);

      const result = {};
    result[result.source.droppableId] = sourceClone;
    result[result.destination.droppableId] = destClone;

    console.log(result.source.droppableId);
    console.log(result.destination.droppableId);

    setTodos(result);
    };

    // --""-- 



console.log(result);
  }


  return (

    <div className='todo-container'>

      <DragDropContext onDragEnd={handleDragEnd}>
      <div className='todo-list'>
        <Droppable droppableId='droppable-1'>
          {(provided ) => (
              <div className='todo-list-created' {...provided.droppableProps} ref={provided.innerRef} >

                {todos.map((todo, index) => (

                  <Todo 
                  text={todo.text}
                  setTodos={setTodos}
                  todo={todo}
                  todos={todos}
                  index={index}
                  />
                ))} 

                </div>

          )} 
        </Droppable>
        <div className='form-wrapper'>
                <Form 
                setInputText={setInputText}  
                inputText={inputText} 
                todos={todos} 
                setTodos={setTodos}
                />
                </div>
        </div>


      </DragDropContext> 

    </div> 
  )
}

export default TodoList
