import React from 'react'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd'

import Todo from './Todo'




function TodoList({ todos, setTodos }) {

  const onDragEnd = (result, todos, setTodos) => {
    if (!result.destination) {
      return; 
    }
    const {source, destination} = result; 
    const todo = todos[source.droppableId];
    const copiedItems = [...todo.items]
  }
  return (



    <div className='todo-container'>
      <DragDropContext >
        <Droppable droppableId='droppable'>
          {(provided) => (
          <ul className='todo-list-created' {...provided.droppableProps} ref={provided.innerRef} >
            {todos.map((todo, index) => (

                  <Todo 
                text={todo.text}
                setTodos={setTodos}
                todo={todo}
                todos={todos}
                index={index}
                />

            ))} 
        </ul>
          )} 

          {provided.placeholder}
        </Droppable>
        <ul className='todo-list-in-progress'>

        </ul>
        <ul className='todo-list-done'>

        </ul>
        </DragDropContext>
    </div> 
  )
}

export default TodoList
