*/



  return (

      <div className="App">

        <header>
          <h1 className='heading'> WELCOME! </h1>
        </header>
        <div className='count-wrapper'>
          <h2> {todosLength} tasks left to do!</h2>
        </div>
        <div className='form-wrapper'>
              <Form 
              setInputText={setInputText}  
              inputText={inputText} 
              todos={todos} 
              setTodos={setTodos}
              />
            </div>
        <div>

        </div>
          <TodoList 
          todos={todos} 
          setTodos={setTodos}
          setInputText={setInputText}  
          inputText={inputText} 
           />
      </div>
  );
}

export default Board;
