import React, { useState, useEffect} from 'react';
import { DragDropContext } from 'react-beautiful-dnd';
import './Styling/Board.css'; 
import Form from './Form';
import Navbar from './Navbar';
import TodoList from './TodoList';
import { v4 as uuidv4 } from "uuid";
import ListForm from './ListForm';
import el from './el';



function Board() {

  const [inputText, setInputText] = useState(""); 
  const [todos, setTodos] = useState([]);
  const [inputTitle, setInputTitle] = useState("");
  const [lists, setLists] = useState([]);

  const [el, setEl] = ([]);


//Save data local
/*
  useEffect(() => {
    getLocalTodos();},
    []);

  useEffect(() => {
    saveLocalTodos();
  }, [todos]);

  const saveLocalTodos = () => {
      localStorage.setItem('todos', JSON.stringify(todos));
    };

  const getLocalTodos = () => {
    if (localStorage.getItem('todos') === null) {
      localStorage.setItem('todos', JSON.stringify([]));
    } else {
     let todoLocal = JSON.parse(localStorage.getItem('todos'));
     setTodos(todoLocal);
    }
    };
    */

    // Save the updated number of cards
    const todosLength = todos.length; 

         // Updates the todos array with the inputed text 
         const submitListHandler = (e) => {
          // Prevents refreshing the browser when the button is clicked
          e.preventDefault();
          setLists([
              ...lists,{
                  title: inputTitle, 
                  id: uuidv4()
              }
          ]);
          setInputTitle(" ");
      };


  return (

      <div className="App">
        <Navbar />
        <header>
          <h1 className='heading'> WELCOME! </h1>
        </header>
        <div className='count-wrapper'>
          <h2> {todosLength} tasks left to do!</h2>
        </div>
        <div>
        <button type="submit" onClick={submitListHandler} >
        New list
      </button>
      {el.map((element) => (
        <el 
        el={el}
        setEl={setEl}
        element={element}
        />
      ))}
        </div>
          <TodoList 
          todos={todos} 
          setTodos={setTodos}
          setInputText={setInputText}  
          inputText={inputText} 
          inputTitle={inputTitle}
          setInputTitle={setInputTitle}
          lists={lists}
          setLists={setLists}
           />
      </div>
  );
}

export default Board;
