import React from 'react'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd'

import Todo from './Todo'




function TodoList({ todos, setTodos }) {

  const handleDragEnd = (result, source, destination) => {
    if(!result.destination) return
    const items = Array.from(todos); 
    const [reorderedItem] = items.splice(result.source.index, 1); 
    items.splice(result.destination.index, 0, reorderedItem);
    console.log(source);
    console.log(destination);

    setTodos(items);

  }
  return (




    <div className='todo-container'>
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId='droppable'>
          {(provided, snapshot) => (
            <div className='list-wrapper' {...provided.droppableProps} ref={provided.innerRef}>
              <div className='todo-list-created'  >
                {todos.map((todo, index) => (

                  <Todo 
                  text={todo.text}
                  setTodos={setTodos}
                  todo={todo}
                  todos={todos}
                  index={index}
                  />

                ))} 

              </div>
              <div className='todo-list-in-progress'>




              </div>
              <div className='todo-list-done'>

              </div>

            </div>


          )} 
        </Droppable>




      </DragDropContext>
    </div> 
  )
}

export default TodoList
